using System;\nusing MySql.Data.MySqlClient;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string connectionString = "Server=yourserver;Database=yourdatabase;Uid=yourusername;Pwd=yourpassword;";\n        \n        try\n        {\n            using (MySqlConnection connection = new MySqlConnection(connectionString))\n            {\n                connection.Open();\n                Console.WriteLine("Connection opened successfully.");\n\n                // Example: Insert data into a table\n                string query = "INSERT INTO yourtable (column1, column2) VALUES (@value1, @value2)";\n                using (MySqlCommand command = new MySqlCommand(query, connection))\n                {\n                    command.Parameters.AddWithValue("@value1", Console.ReadLine());\n                    command.Parameters.AddWithValue("@value2", Console.ReadLine());\n\n                    int rowsAffected = command.ExecuteNonQuery();\n                    Console.WriteLine($"{rowsAffected} row(s) inserted.");\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}