using Excel = Microsoft.Office.Interop.Excel;\n\nExcel.Application xlApp = new Excel.Application();\ntry\n{\n    // Prevent Excel from stealing focus\n    xlApp.Visible = false;\n    xlApp.ScreenUpdating = false;\n    xlApp.DisplayAlerts = false;\n    xlApp.Interactive = false; // Prevent any interactive behavior\n\n    // Disable the display of status bar and other elements that might cause flicker\n    xlApp.StatusBar = false;\n    xlApp.UserControl = true;\n\n    Excel.Workbooks workBooks = xlApp.Workbooks;\n    Excel.Workbook xlWorkBook = workBooks.Open(@"path_to_your_workbook");\n    Excel.Sheets sheets = xlWorkBook.Sheets;\n    \n    // Assuming you want to insert into the first sheet\n    Excel.Worksheet worksheet = (Excel.Worksheet)sheets.get_Item(1);\n\n    // Insert image\n    string filename = @"path_to_your_image_file";\n    Excel.Pictures pictures = worksheet.Pictures(Type.Missing);\n    Excel.Picture picture = pictures.Insert(filename, Type.Missing);\n\n    // Adjust the position and size of the picture as needed\n    xlWorkBook.Save();\n}\ncatch (Exception ex)\n{\n    // Handle exceptions here\n    Console.WriteLine("Error: " + ex.Message);\n}\nfinally\n{\n    // Clean up everything once completed or failed\n    if (xlApp != null)\n    {\n        xlApp.Workbooks.Close();\n        xlApp.Quit();\n\n        System.Runtime.InteropServices.Marshal.ReleaseComObject(worksheet);\n        System.Runtime.InteropServices.Marshal.ReleaseComObject(xlWorkBook);\n        System.Runtime.InteropServices.Marshal.ReleaseComObject(workBooks);\n\n        // Reset flags and release Excel application\n        xlApp.Interactive = true;\n        xlApp.Visible = false;\n\n        System.Runtime.InteropServices.Marshal.ReleaseComObject(xlApp);\n    }\n}