using System;\nusing IBM.Data.Informix;\n\nclass Program\n{\n    static void Main()\n    {\n        using (IfxConnection c = new IfxConnection("..."))\n        {\n            c.Open();\n            Using (IfxCommand cmd = c.CreateCommand())\n            {\n                cmd.CommandText = "SELECT id, data INTO temp.id, temp.bloblocator FROM aaa";\n                \n                // Adjust the command to select BLOB as locator\n                cmd.CommandText = "OPEN cur FOR SELECT id,data FROM aaa USING CURSOR";\n                \n                using (IfxCursor cursor = new IfxCursor(cmd))\n                {\n                    if (cursor.Open())\n                    {\n                        while (cursor.Read())\n                        {\n                            int id = cursor.GetInt32(0);\n                            IfxBlob blobLocator;\n                            \n                            // Retrieve the BLOB as a locator\n                            blobLocator = cursor.GetBlob(1);\n                            \n                            Console.WriteLine($"ID: {id}, Blob: {blobLocator}");\n                        }\n                    }\n                }\n            }\n        }\n    }\n}