using System;\nusing System.Collections.Generic;\nusing System.IO;\n\nclass DirectoryScanner\n{\n    static void Main(string[] args)\n    {\n        string path = @"C:\YourDrive"; // specify your drive or folder path\n        string snapshotFile1 = "Snapshot1.txt";\n        \n        // First run: Create the initial snapshot\n        WriteSnapshot(path, snapshotFile1);\n        \n        Console.WriteLine("Initial snapshot created. You can now modify and create files.");\n        Console.Write("Press Enter to capture a new snapshot for comparison...");\n        Console.ReadLine();\n\n        string snapshotFile2 = "Snapshot2.txt";\n        // Second run: Create a new snapshot\n        WriteSnapshot(path, snapshotFile2);\n\n        CompareSnapshots(snapshotFile1, snapshotFile2);\n    }\n\n    static void WriteSnapshot(string path, string file)\n    {\n        using (StreamWriter writer = new StreamWriter(file))\n        {\n            foreach (string dir in Directory.GetDirectories(path, "*", SearchOption.AllDirectories))\n            {\n                writer.WriteLine(dir);\n            }\n            \n            foreach (string fileInDir in Directory.GetFiles(path, "*.*", SearchOption.AllDirectories))\n            {\n                writer.WriteLine(fileInDir);\n            }\n        }\n\n        Console.WriteLine("Snapshot written to " + file);\n    }\n\n    static void CompareSnapshots(string snapshot1, string snapshot2)\n    {\n        HashSet<string> files1 = new HashSet<string>(File.ReadAllLines(snapshot1));\n        HashSet<string> files2 = new HashSet<string>(File.ReadAllLines(snapshot2));\n\n        var removedFiles = files1.Except(files2); // Files in 1 but not 2\n        var addedFiles = files2.Except(files1);   // Files in 2 but not 1\n\n        Console.WriteLine("Removed Files:");\n        foreach (var file in removedFiles)\n            Console.WriteLine(file);\n\n        Console.WriteLine("\nAdded Files:");\n        foreach (var file in addedFiles)\n            Console.WriteLine(file);\n    }\n}