[System.Web.Services.WebMethod]\npublic static string LoadDialog()\n{\n    using (StringWriter output = new StringWriter())\n    {\n        using (HtmlTextWriter writer = new HtmlTextWriter(output))\n        {\n            Page pageHolder = new Page(); // Create a new page for the control\n            var panel = (Panel)pageHolder.LoadControl("~/YourUserControl.ascx"); // Load your heavy user control if needed\n            pageHolder.Controls.Add(panel); // Add to page holder\n\n            panel.RenderControl(writer); // Render the control to a StringWriter\n        }\n        return output.ToString();  // Return the rendered result as string\n    }\n}