using (var connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n    using (var command = new SqlCommand("UPDATE Documents SET DocumentText = @DocumentText WHERE DocumentId = @DocumentId", connection))\n    {\n        command.Parameters.AddWithValue("@DocumentId", documentId);\n        \n        var fileStream = new FileStream("input.txt", FileMode.Open, FileAccess.Read);\n        command.Parameters.Add(new SqlParameter("@DocumentText", SqlDbType.NVarChar) { Value = fileStream });\n\n        command.ExecuteNonQuery();\n    }\n}