using (var context = new YourDataContext())\n{\n    var ordersWithProductNames = \n        from order in context.Orders\n        join product in context.Products on order.ProductID equals product.ProductID\n        select new {\n            OrderID = order.OrderID,\n            ProductName = product.Name // Fetch only the Name field of the product\n            // Add other fields from order or product as needed\n        };\n\n    var results = ordersWithProductNames.ToList();\n    \n    foreach (var item in results)\n    {\n        Console.WriteLine($"Order ID: {item.OrderID}, Product Name: {item.ProductName}");\n    }\n}