using (var connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n    using (var command = new SqlCommand("SELECT DocumentId, DocumentText FROM Documents WHERE DocumentId = @DocumentId", connection))\n    {\n        command.Parameters.AddWithValue("@DocumentId", documentId);\n\n        using (SqlDataReader reader = command.ExecuteReader(CommandBehavior.SequentialAccess))\n        {\n            if (reader.Read())\n            {\n                long bytesToRead;\n                byte[] buffer = new byte[1024]; // Adjust this buffer size as needed\n\n                using (var streamWriter = new StreamWriter("output.txt", append: false))\n                {\n                    do\n                    {\n                        // Read the data in chunks of 1024 bytes.\n                        bytesToRead = reader.GetBytes(1, 0, buffer, 0, buffer.Length);\n                        streamWriter.Write(System.Text.Encoding.UTF8.GetString(buffer, 0, (int)bytesToRead));\n                    } while (bytesToRead > 0);\n                }\n            }\n        }\n    }\n}