using System.Windows;\nusing System.Windows.Input;\n\nnamespace DraggableExample\n{\n    public partial class MainWindow : Window\n    {\n        private Point _startPoint;\n\n        public MainWindow()\n        {\n            InitializeComponent();\n        }\n\n        private void draggableGrid_MouseDown(object sender, MouseButtonEventArgs e)\n        {\n            if (e.LeftButton == MouseButtonState.Pressed)\n            {\n                // Store the start point for future movements\n                _startPoint = e.GetPosition(null);\n            }\n        }\n\n        private void draggableGrid_MouseMove(object sender, MouseEventArgs e)\n        {\n            if (e.LeftButton == MouseButtonState.Pressed)\n            {\n                Point currentPosition = e.GetPosition(null);\n                double deltaX = currentPosition.X - _startPoint.X;\n                double deltaY = currentPosition.Y - _startPoint.Y;\n\n                // Update the position of the Grid\n                Canvas.SetLeft(draggableGrid, Canvas.GetLeft(draggableGrid) + deltaX);\n                Canvas.SetTop(draggableGrid, Canvas.GetTop(draggableGrid) + deltaY);\n\n                // Update the start point for future movements\n                _startPoint = currentPosition;\n            }\n        }\n\n        private void draggableGrid_MouseUp(object sender, MouseButtonEventArgs e)\n        {\n            // Reset the process on mouse up\n            _startPoint = new Point();\n        }\n    }\n}