using System;\nusing System.Data.SqlClient;\nusing System.Reflection;\n\npublic void AddApplicationNameToConnectionString(SqlConnection sqlConnection, string applicationName)\n{\n    // Get the private field '_connectionString' within SqlConnection\n    FieldInfo connectionStringField = typeof(SqlConnection).GetField("_connectionString", BindingFlags.Instance | BindingFlags.NonPublic);\n\n    if (connectionStringField != null)\n    {\n        string originalConnectionString = (string)connectionStringField.GetValue(sqlConnection);\n        var builder = new SqlConnectionStringBuilder(originalConnectionString)\n        {\n            ApplicationName = applicationName\n        };\n        connectionStringField.SetValue(sqlConnection, builder.ConnectionString);\n    }\n}