using System;\nusing System.Text.RegularExpressions;\n\nclass WikiParser {\n    public static string Parse(string wikiText) {\n        // Example: Convert bold tags\n        wikiText = Regex.Replace(wikiText, "'''''(.*?)'''''", "<strong>$1</strong>");\n        wikiText = Regex.Replace(wikiText, "'''(.*?)'''", "<strong>$1</strong>");\n\n        // Example: Convert italics\n        wikiText = Regex.Replace(wikiText, "''(.*?)''", "<em>$1</em>");\n\n        return wikiText;\n    }\n\n    public static void Main() {\n        string input = "This is '''bold text''' and ''italic text''.";\n        string output = Parse(input);\n        Console.WriteLine(output);  // Output: This is <strong>bold text</strong> and <em>italic text</em>.\n    }\n}