using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        string serviceName = "YourServiceName";\n        string displayName = "Your Service Display Name";\n        string executablePath = @"C:\Path\To\ThirdPartyExecutable.exe";\n\n        try\n        {\n            InstallService(serviceName, displayName, executablePath);\n            Console.WriteLine("Service installed successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Failed to install service: " + ex.Message);\n        }\n    }\n\n    private static void InstallService(string serviceName, string displayName, string executablePath)\n    {\n        ProcessStartInfo processInfo = new ProcessStartInfo\n        {\n            FileName = "sc.exe",\n            Arguments = $"create {serviceName} binPath= \"{executablePath}\" DisplayName= \"{displayName}\" start= auto",\n            Verb = "runas", // Requires admin privileges\n            CreateNoWindow = true,\n            UseShellExecute = false\n        };\n\n        var process = Process.Start(processInfo);\n        process?.WaitForExit();\n\n        if (process != null && process.ExitCode != 0)\n        {\n            throw new Exception($"Failed to install service, exit code: {process.ExitCode}");\n        }\n    }\n}