using System;\nusing Microsoft.Security.Application;\n\npublic class Example {\n    public static void Main() {\n        string html = "I'm building up a string on the server that is getting put into a javascript variable on the client.";\n        string safeHtml = AntiXssEncoder.HtmlEncode(html, true);\n        \n        // Output to be used in JavaScript\n        Console.WriteLine("var myVar = \"{0}\";", safeHtml.Replace("\"", "\\\""));\n    }\n}