using System;\n\npublic static class FileSizeFormatter\n{\n    public static string FormatSize(long fileSize)\n    {\n        if (fileSize < 1024) return fileSize + " B";\n\n        int order = (int)Math.Log(fileSize, 1024);\n        var units = new[] { "KB", "MB", "GB", "TB", "PB", "EB", "ZB", "YB" };\n        \n        double size = fileSize / Math.Pow(1024, order);\n\n        return string.Format("{0:0.##} {1}", size, units[order - 1]);\n    }\n}\n\n// Example usage:\npublic class Program\n{\n    public static void Main()\n    {\n        long fileSize = 7326629;\n        Console.WriteLine(FileSizeFormatter.FormatSize(fileSize)); // Output: 6.98 MB\n    }\n}