using System.Data.OleDb;\n\n// Connection string for your Access database\nstring connectionString = "Provider=Microsoft.ACE.OLEDB.12.0;Data Source=C:\\path\\to\\yourdatabase.accdb;";\nusing (OleDbConnection conn = new OleDbConnection(connectionString))\n{\n    conn.Open();\n    \n    // Command to execute a saved query from Access\n    string queryName = "YourSavedQueryName";\n    using (OleDbCommand cmd = new OleDbCommand($"SELECT * FROM [{queryName}]", conn))\n    {\n        using (OleDbDataAdapter adapter = new OleDbDataAdapter(cmd))\n        {\n            System.Data.DataTable resultsTable = new System.Data.DataTable();\n            adapter.Fill(resultsTable);\n            \n            // Work with resultsTable as needed\n        }\n    }\n}