try\n{\n    using (SqlConnection conn = Connection())\n    {\n        conn.Open();\n        \n        using (SqlTransaction sqlTrans = conn.BeginTransaction())\n        {\n            try\n            {\n                using (SqlCommand cmd1 = new SqlCommand("Stored_Proc_1", conn, sqlTrans))\n                {\n                    cmd1.CommandType = CommandType.StoredProcedure;\n                    cmd1.ExecuteNonQuery();\n                }\n\n                using (SqlCommand cmd2 = new SqlCommand("Stored_Proc_2", conn, sqlTrans))\n                {\n                    cmd2.CommandType = CommandType.StoredProcedure;\n                    cmd2.ExecuteNonQuery();\n                }\n\n                sqlTrans.Commit();\n            }\n            catch (SqlException ex)\n            {\n                sqlTrans.Rollback();\n\n                // Re-throw or log the exception as needed\n                throw;  // Or you can handle it specifically here if required\n            }\n        }        \n    }\n}\ncatch (SqlException ex)\n{\n    // Handle and log the exception. The error message should be clean now.\n}