using System;\nusing System.Windows.Forms;\n\npublic class IrReceiverForm : Form\n{\n    public IrReceiverForm()\n    {\n        this.KeyDown += new KeyEventHandler(IrReceiverForm_KeyDown);\n        this.Text = "IR Receiver Test";\n        this.Size = new System.Drawing.Size(300, 200);\n    }\n\n    private void IrReceiverForm_KeyDown(object sender, KeyEventArgs e)\n    {\n        // Display the key pressed by IR remote\n        MessageBox.Show($"Key Pressed: {e.KeyCode}");\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new IrReceiverForm());\n    }\n}