using System;\n\npublic class ItemRanker\n{\n    public static double CalculateScore(int votes, DateTime submissionTime, int gravity = 1.8)\n    {\n        var ageInHours = (DateTime.UtcNow - submissionTime).TotalHours;\n        var score = votes - 1; // subtracting 1 to negate upvotes so that items with only one upvote aren't inadvertently given a higher starting score\n        return score / Math.Pow(ageInHours + 2, gravity);\n    }\n\n    public static void Main()\n    {\n        DateTime submissionTime = DateTime.UtcNow.AddHours(-5); // Example: submitted 5 hours ago\n        int votes = 30; // Example: item has 30 upvotes\n\n        double score = CalculateScore(votes, submissionTime);\n        Console.WriteLine($"The calculated score is: {score}");\n    }\n}