protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Check if client supports gzip compression\n        var acceptEncoding = Request.Headers["Accept-Encoding"];\n        bool canCompress = false;\n        if ((acceptEncoding != null) && (acceptEncoding.Contains("gzip")))\n            canCompress = true;\n\n        if (canCompress)\n        {\n            Response.AppendHeader("Content-encoding", "gzip");\n            // Add a filter to the response stream\n            Response.Filter = new System.IO.Compression.GZipStream(Response.Filter, \n                System.IO.Compression.CompressionMode.Compress);\n        }\n\n        BindResults(); // Make sure this method handles compressed and uncompressed content properly\n    }\n}\n\nprivate void BindResults()\n{\n    // Ensure that data binding logic works regardless of compression settings\n    yourDataSource = GetDataFromDatabase();\n    ResultsRepeater.DataSource = yourDataSource;\n    ResultsRepeater.DataBind();\n\n    if (yourDataSource.Count == 0)\n        ResultsMessage.Text = "0 results returned";\n}