// Include namespaces\n   using DockSample;\n   using WeifenLuo.WinFormsUI.Docking;\n\n   public partial class MainForm : Form\n   {\n       private ToolWindow m_toolWindow; // This will be our tool window\n\n       public MainForm()\n       {\n           InitializeComponent();\n\n           // Create a new instance of the dock panel where tools are docked\n           DockPanel dockPanel = new DockPanel();\n           \n           // Configure and add it to your form\n           dockPanel.Dock = DockStyle.Fill;\n           this.Controls.Add(dockPanel);\n\n           // Create a custom tool window derived from DockContent\n           m_toolWindow = new ToolWindow();\n\n           // Set properties like caption text etc.\n           m_toolWindow.Text = "Toolbox";\n\n           // Dock the tool window as floating initially or docked to any side of your form\n           m_toolWindow.DockState = DockState.DockLeft;\n\n           // Add this custom window to the dock panel\n           m_toolWindow.Show(dockPanel);\n       }\n   }\n\n   // Define a class that derives from DockContent for your tools\n   public class ToolWindow : DockContent\n   {\n       private TreeView treeView;\n\n       public ToolWindow()\n       {\n           // Initialize and add components here\n           treeView = new TreeView();\n           \n           // Add some nodes\n           treeView.Nodes.Add("Component 1");\n           treeView.Nodes.Add("Component 2");\n\n           // Set the Dock property to fill so it resizes with the container\n           treeView.Dock = DockStyle.Fill;\n           \n           // Add the TreeView control to this content window\n           this.Controls.Add(treeView);\n           \n           // Optionally set other properties like auto-hide etc.\n           this.AutoHidePortion = 80;\n       }\n   }