using System;\nusing System.IO;\nusing System.Windows.Media.Imaging;\n\nclass Program\n{\n    static void Main()\n    {\n        var bitmapFrame = BitmapFrame.Create(new Uri("path_to_your_image.jpg"));\n        foreach (var query in from item in bitmapFrame.Metadata.Query ?? Enumerable.Empty<KeyValuePair<string, object>>()\n                              select new { Key = item.Key, Value = item.Value })\n        {\n            Console.WriteLine($"{query.Key}: {query.Value}");\n        }\n    }\n}