public void InsertCustomer(string name, int? age, string address)\n{\n    StringBuilder queryInsert = new StringBuilder();\n    queryInsert.Append("insert into Customers(name, age, address) values (@name, @age, @address)");\n\n    this.myDataContext.ExecuteCommand(queryInsert.ToString(),\n        new SqlParameter("@name", name ?? (object)DBNull.Value),\n        new SqlParameter("@age", age.HasValue ? (object)age : DBNull.Value),\n        new SqlParameter("@address", address ?? (object)DBNull.Value));\n}