using System;\nusing System.Net;\nusing System.Net.Mail;\n\npublic void SendAuthenticatedEmail()\n{\n    var smtpClient = new SmtpClient("smtp.yourserver.com")\n    {\n        Port = 587, // Common port for SMTP with TLS\n        Credentials = new NetworkCredential("your-email@example.com", "yourpassword"),\n        EnableSsl = true, // Important to enable SSL/TLS\n        DeliveryMethod = SmtpDeliveryMethod.Network,\n        UseDefaultCredentials = false, // Set this to false to use specified credentials\n    };\n\n    var mailMessage = new MailMessage\n    {\n        From = new MailAddress("your-email@example.com"),\n        Subject = "Test Email",\n        Body = "This is a test email from an authenticated SMTP client."\n    };\n    \n    mailMessage.To.Add(new MailAddress("recipient-email@example.com"));\n\n    try\n    {\n        smtpClient.Send(mailMessage);\n        Console.WriteLine("Email sent successfully.");\n    }\n    catch (Exception ex)\n    {\n        Console.Error.WriteLine($"Failed to send email: {ex.Message}");\n    }\n}