using System;\n\npublic class DifferenceCalculator {\n    public static decimal CalculateDifference(decimal num1, decimal num2) {\n        return Math.Abs(num1 - num2);\n    }\n\n    public static void Main() {\n        Console.WriteLine(CalculateDifference(100m, 25m));  // Output: 75\n        Console.WriteLine(CalculateDifference(100m, -25m)); // Output: 125\n        Console.WriteLine(CalculateDifference(-100m, -115m));// Output: 15\n        Console.WriteLine(CalculateDifference(-500m, 100m)); // Output: 600\n    }\n}