using System;\nusing Microsoft.Web.Administration;\n\npublic class DomainManager\n{\n    public void AddHostHeader(string siteName, string bindingInformation, string hostname)\n    {\n        using (var serverManager = new ServerManager())\n        {\n            var site = serverManager.Sites[siteName];\n            if (site != null)\n            {\n                bool hostExists = false;\n                foreach (var binding in site.Bindings)\n                {\n                    if (binding.Host == hostname)\n                    {\n                        hostExists = true;\n                        break;\n                    }\n                }\n\n                if (!hostExists)\n                {\n                    var newBinding = site.Bindings.Add(bindingInformation, hostname);\n                    serverManager.CommitChanges();\n                }\n            }\n        }\n    }\n}