using System.Net;\n\npublic class WebServiceClientStartup\n{\n    public static void Configure()\n    {\n        // Disable proxy usage by setting it to null\n        WebRequest.DefaultWebProxy = null;\n        \n        // Alternatively, if using HttpClient in later .NET versions\n        var handler = new HttpClientHandler() { UseProxy = false };\n        var client = new HttpClient(handler);\n\n        // Use the 'client' for web requests\n    }\n}