using (var db = new MyDataContext())\n{\n    var newPerson = new Person { Name = "Alice" };\n    db.Persons.InsertOnSubmit(newPerson);\n\n    // Manually keep track of the entities you add\n    List<Person> NewlyAddedPeople = new List<Person> { newPerson };\n\n    // Query newly added objects using LINQ to Objects\n    foreach (var person in newlyAddedPeople)\n    {\n        Console.WriteLine(person.Name);\n    }\n\n    db.SubmitChanges(); // Save changes to database\n}