using System;\nusing Oracle.DataAccess.Client; // This requires Oracle Data Provider for .NET\n\npublic class OracleExample\n{\n    public static void Main()\n    {\n        string constr = "User Id=myUsername;Password=myPassword;Data Source=MyOracleDB";\n\n        using (OracleConnection con = new OracleConnection(constr))\n        {\n            try\n            {\n                con.Open();\n                Console.WriteLine("Connected to Oracle Database successfully!");\n\n                // Example of executing a simple query\n                using (OracleCommand cmd = new OracleCommand("SELECT SYSDATE FROM dual", con))\n                {\n                    string dateFromDb = Convert.ToString(cmd.ExecuteScalar());\n                    Console.WriteLine("Date from Oracle: " + dateFromDb);\n                }\n            }\n            catch (Exception e)\n            {\n                Console.WriteLine("Error connecting to Oracle Database: " + e.Message);\n            }\n        }\n    }\n}