using Microsoft.Diagnostics.Runtime;\nusing System;\n\nnamespace ObjectSizeCalculator\n{\n    class Program\n    {\n        static void Main()\n        {\n            var testData = new YourClass(); // Replace with your class\n\n            using (var dataTarget = DataTarget.CreateFromCurrentProcess())\n            {\n                ClrRuntime runtime = dataTarget.ClrVersions[0].CreateRuntime();\n                ClrHeap heap = runtime.GetHeap();\n\n                ulong objAddress = GCHandles.Alloc(testData, GCHandleType.Pinned);\n                ClrObject clrObj = heap.GetObject(objAddress);\n\n                Console.WriteLine($"Size of the object: {clrObj.Size} bytes");\n                GCHandles.Free(objAddress);\n            }\n        }\n    }\n\n    public class YourClass\n    {\n        // Define your properties and fields as needed\n        public int Number { get; set; }\n        public string Text { get; set; }\n    }\n}