using ComponentSpace.SAML2;\nusing System.Web;\n\npublic void InitiateSamlAuthRequest(HttpContext context)\n{\n    // Configure your SP settings\n    string spEntityId = "https://yoursp.com/metadata";\n    string idpUrl = "https://idp.com/login";  // IdP's SSO endpoint\n\n    // Create a SAML request\n    SamlAuthenticationRequest samlAuthnRequest;\n    SamlServiceProviderConfiguration spConfig = new SamlServiceProviderConfiguration(spEntityId);\n\n    samlAuthnRequest = new SamlAuthenticationRequest(spConfig, null);\n\n    // Redirect the user to the IdP with the SAML request\n    string redirectUrl = idpUrl + "?SAMLRequest=" + HttpUtility.UrlEncode(samlAuthnRequest.GetEncodedRequest());\n    context.Response.Redirect(redirectUrl);\n}