using System;\nusing System.Windows.Forms;\n\nnamespace FrontEndApp\n{\n    public partial class MainForm : Form\n    {\n        // Assuming your backend has a class 'DataProcessor' with method 'Calculate'\n        private DataProcessor _dataProcessor = new DataProcessor();\n\n        public MainForm()\n        {\n            InitializeComponent();\n            \n            // Initialize application settings, load from file if needed\n            LoadSettings();\n        }\n\n        private void btnSelectFiles_Click(object sender, EventArgs e)\n        {\n            using (OpenFileDialog openFileDialog = new OpenFileDialog())\n            {\n                openFileDialog.Multiselect = true;\n                if (openFileDialog.ShowDialog() == DialogResult.OK &&\n                    openFileDialog.FileNames.Length > 0 &&\n                    openFileDialog.FileNames.Length <= 3)\n                {\n                    // Pass selected file paths to your backend\n                    _dataProcessor.SetInputFiles(openFileDialog.FileNames);\n                }\n            }\n        }\n\n        private void btnRunCalculation_Click(object sender, EventArgs e)\n        {\n            try\n            {\n                string result = _dataProcessor.Calculate();\n                txtStatus.Text += "Calculation Result: " + result + Environment.NewLine;\n            }\n            catch (Exception ex)\n            {\n                MessageBox.Show("An error occurred: " + ex.Message);\n            }\n        }\n\n        private void LoadSettings()\n        {\n            // Example method to load settings from a configuration file or elsewhere\n        }\n\n        private void btnSaveSettings_Click(object sender, EventArgs e)\n        {\n            // Example method to save settings\n        }\n    }\n}