using System;\nusing System.IO;\n\npublic class PlaylistGenerator {\n    public static void GeneratePlaylist(string outputPath, string[] filePaths) {\n        using (StreamWriter writer = new StreamWriter(outputPath)) {\n            writer.WriteLine("<ASX Version=\"3.0\">");\n            foreach (string path in filePaths) {\n                writer.WriteLine($"<ENTRY><REF HREF=\"{path}\" /></ENTRY>");\n            }\n            writer.WriteLine("</ASX>");\n        }\n    }\n\n    public static void Main(string[] args) {\n        string[] mediaFiles = { "http://yourserver/path/to/media1.wmv", "http://yourserver/path/to/media2.wmv" };\n        GeneratePlaylist("C:\\Playlists\\playlist.asx", mediaFiles);\n    }\n}