using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string sourcePath = @"Z:\folder";\n        string destinationPath = @"\\computername\sharedfolder";\n\n        // Check if directories exist\n        if (Directory.Exists(sourcePath) && Directory.Exists(destinationPath))\n        {\n            try\n            {\n                DirectoryInfo sourceDir = new DirectoryInfo(sourcePath);\n                DirectoryInfo destDir = new DirectoryInfo(destinationPath);\n\n                // Example of copying each file to the destination directory\n                foreach (FileInfo file in sourceDir.GetFiles())\n                {\n                    string destFilePath = Path.Combine(destDir.FullName, file.Name);\n                    file.CopyTo(destFilePath, true); // Overwrite if exists\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"An error occurred: {ex.Message}");\n            }\n        }\n        else\n        {\n            Console.WriteLine("One or both of the directories does not exist.");\n        }\n    }\n}