using System;\nusing System.Globalization;\nusing System.Threading;\n\npublic void SetArabicGregorianCulture()\n{\n    // Create a CultureInfo object for Arabic (Saudi Arabia)\n    CultureInfo ci = new CultureInfo("ar-SA");\n\n    // Explicitly set the Calendar to GregorianCalendar\n    ci.DateTimeFormat.Calendar = new GregorianCalendar();\n\n    // Set this culture as the current thread's culture and UI culture\n    Thread.CurrentThread.CurrentCulture = ci;\n    Thread.CurrentThread.CurrentUICulture = ci;\n}