using System;\nusing System.Collections.Generic;\nusing System.Data;\n\npublic class PersonRecord\n{\n    public string Id { get; set; }\n    public string FirstName { get; set; }\n    public string Address { get; set; }\n    public string City { get; set; }\n\n    // Constructor for easy initialization\n    public PersonRecord(string id, string firstName, string address, string city)\n    {\n        Id = id;\n        FirstName = firstName;\n        Address = address;\n        City = city;\n    }\n}\n\npublic class DatabaseService\n{\n    public List<PersonRecord> GetPersonRecords(IDataReader dataReader)\n    {\n        var personList = new List<PersonRecord>();\n\n        using (dataReader)\n        {\n            while (dataReader.Read())\n            {\n                // Assuming columns in the same order as specified\n                string id = dataReader.GetString(0);\n                string firstName = dataReader.GetString(1);\n                string address = dataReader.GetString(2);\n                string city = dataReader.GetString(3);\n\n                personList.Add(new PersonRecord(id, firstName, address, city));\n            }\n        }\n\n        return personList;\n    }\n}