using System;\nusing System.Text.RegularExpressions;\nusing System.Web;\n\npublic static class HtmlStripper\n{\n    public static string StripHtmlTags(string html)\n    {\n        // First, decode HTML entities (like &amp; &gt; etc.)\n        var decoded = HttpUtility.HtmlDecode(html);\n        \n        // Create a TagRemover to remove tags\n        using (var tagRemover = new System.IO.StringReader(decoded))\n        using (var reader = new System.Xml.XmlTextReader(tagRemover))\n        {\n            reader.WhitespaceHandling = System.Xml.WhitespaceHandling.None;\n            string result = "";\n            while (reader.Read())\n            {\n                if (reader.NodeType == System.Xml.XmlNodeType.Text)\n                    result += reader.Value;\n                else if (reader.NodeType == System.Xml.XmlNodeType.EntityReference)\n                    result += "&" + reader.Name + ";";\n            }\n            return result;\n        }\n    }\n}