public void ResetDataSet(MyDataSet sharedDS)\n{\n    // Clear the table(s) in the DataSet\n    sharedDS.Table1.Clear();\n\n    // If you need to reset any auto-increment settings specifically\n    // Note: Typically, clearing will suffice as auto-increment resets itself\n    foreach (DataColumn col in sharedDS.Table1.Columns)\n    {\n        if (col.AutoIncrement)\n        {\n            col.AutoIncrementSeed = 0;\n            col.AutoIncrementStep = 1; // Ensure the step value is set properly\n        }\n    }\n\n    // Optionally, to completely refresh and ensure no orphaned constraints or relations cause issues\n    sharedDS.AcceptChanges(); // Accept changes before resetting\n\n    // If you have multiple tables within the DataSet that need similar treatment\n    // Repeat the above steps for each DataTable within the DataSet\n}