using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic class CustomForm : Form\n{\n    private Button closeButton;\n    private Button minimizeButton;\n    private Button maximizeButton;\n\n    public CustomForm()\n    {\n        #region Setup Form\n        this.FormBorderStyle = FormBorderStyle.None; // Remove default window border\n        this.StartPosition = FormStartPosition.CenterScreen;\n        this.BackColor = Color.LightBlue; // Set the form's backcolor\n        #endregion\n\n        #region Close button setup\n        closeButton = new Button();\n        closeButton.Text = "X";\n        closeButton.Size = new Size(30, 30);\n        closeButton.Location = new Point(this.ClientSize.Width - 45, 10); // Adjust position as needed\n        closeButton.BackColor = Color.Red;\n        closeButton.ForeColor = Color.White;\n        closeButton.FlatStyle = FlatStyle.Flat;\n        closeButton.FlatAppearance.BorderSize = 0;\n        closeButton.Click += (s, e) => this.Close();\n        this.Controls.Add(closeButton);\n        #endregion\n\n        #region Maximize button setup\n        maximizeButton = new Button();\n        maximizeButton.Text = "â–¡"; // Unicode character for square symbol\n        maximizeButton.Size = new Size(30, 30);\n        maximizeButton.Location = new Point(this.ClientSize.Width - 85, 10); // Adjust position as needed\n        maximizeButton.BackColor = Color.DodgerBlue;\n        maximizeButton.ForeColor = Color.White;\n        maximizeButton.FlatStyle = FlatStyle.Flat;\n        maximizeButton.FlatAppearance.BorderSize = 0;\n        maximizeButton.Click += (s, e) =>\n        {\n            if (this.WindowState == FormWindowState.Maximized)\n                this.WindowState = FormWindowState.Normal;\n            else\n                this.WindowState = FormWindowState.Maximized;\n        };\n        this.Controls.Add(maximizeButton);\n        #endregion\n\n        #region Minimize button setup\n        minimizeButton = new Button();\n        minimizeButton.Text = "_";\n        minimizeButton.Size = new Size(30, 30);\n        minimizeButton.Location = new Point(this.ClientSize.Width - 125, 10); // Adjust position as needed\n        minimizeButton.BackColor = Color.Green;\n        minimizeButton.ForeColor = Color.White;\n        minimizeButton.FlatStyle = FlatStyle.Flat;\n        minimizeButton.FlatAppearance.BorderSize = 0;\n        minimizeButton.Click += (s, e) => this.WindowState = FormWindowState.Minimized;\n        this.Controls.Add(minimizeButton);\n        #endregion\n    }\n\n    // Adjust button positions when the form is resized.\n    protected override void OnResize(EventArgs e)\n    {\n        base.OnResize(e);\n\n        closeButton.Location = new Point(this.ClientSize.Width - 45, 10);\n        maximizeButton.Location = new Point(this.ClientSize.Width - 85, 10);\n        minimizeButton.Location = new Point(this.ClientSize.Width - 125, 10);\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new CustomForm());\n    }\n}