using System;\nusing System.Web;\n\npublic class Example\n{\n    public void GetProtocolHostAndPort()\n    {\n        // Assuming this code is running in an ASP.NET context where Request is available.\n        Uri requestUrl = HttpContext.Current.Request.Url;\n\n        string protocol = requestUrl.Scheme; // e.g., "http"\n        string host = requestUrl.Host;       // e.g., "www.mywebsite.com"\n        int port = requestUrl.Port;         // e.g., 80\n\n        // Constructing the desired URL part\n        string protocolHostPort = $"{protocol}://{host}:{port}";\n\n        Console.WriteLine(protocolHostPort); // Output: http://www.mywebsite.com:80\n    }\n}