using mshtml;\n\n// Assuming 'theDoc' is your HTMLDocumentClass instance\nHTMLDocument doc = (HTMLDocument)theDoc;\n\n// Method 1: Find by ID - if known, replace "ButtonID" with actual ID\nIHTMLElement buttonById = theDoc.getElementById("ButtonID");\nif (buttonById != null)\n{\n    ((HTMLInputElement)buttonById).click();\n}\nelse\n{\n    // Method 2: Use JavaScript to find by name or innerText (less reliable without unique attributes)\n    string script = "document.querySelector('input[type=\"submit\"][value=\"Web Search\"]').click();";\n    doc.parentWindow.execScript(script, "JavaScript");\n\n    // Alternatively, using XPath (if available in your environment)\n    // IHTMLDocument2 htmlDoc2 = (IHTMLDocument2)theDoc;\n    // HTMLCollection nodes = htmlDoc2.all.tags("input");\n    // foreach (IHTMLElement element in nodes)\n    // {\n    //     if (((HTMLInputElement)element).value == "Web Search")\n    //     {\n    //         ((HTMLInputElement)element).click();\n    //         break;\n    //     }\n    // }\n}