using System;\nusingSharpMap.Forms;\n\npublic class MainForm : Form\n{\n    private MapBox mapControl;\n\n    public MainForm()\n    {\n        InitializeComponent();\n        LoadShapeFile(mapControl);\n    }\n\n    private void InitializeComponent()\n    {\n        this.mapControl = new MapBox();\n        this.SuspendLayout();\n\n        // Setup the map control\n        this.mapControl.Map.BackColor = System.Drawing.Color.LightBlue;\n        this.Controls.Add(this.mapControl);\n\n        this.ResumeLayout(false);\n    }\n\n    private void LoadShapeFile(MapBox map)\n    {\n        var layer = new SharpMap.Layers.VectorLayer("NOAA ENC") { DataSource = new SharpMap.Data.Providers.Ogr(@"path\to\s-57file.enc", "S-57") };\n        map.Map.Layers.Add(layer);\n        map.Refresh();\n    }\n    \n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new MainForm());\n    }\n}