public class UserRepository : CachedRepositoryBase {\n    private readonly MyDataContext _context;\n\n    public UserRepository(ICacheProvider cacheProvider) \n        : base(cacheProvider) {\n        _context = new MyDataContext();\n    }\n\n    public List<User> GetAllUsers() {\n        return GetData("AllUsers", LoadUsers, 3600); // Cache for 1 hour\n    }\n\n    private List<User> LoadUsers() {\n        return (from user in _context.Users select user).ToList();\n    }\n}