using Microsoft.Win32;\n\n// Open the registry key\nRegistryKey APRegsitry = Registry.CurrentUser.OpenSubKey("Software\\YourAppPath");\n\nif (APRegsitry != null)\n{\n    object regValue = APRegsitry.GetValue("apTime");\n    if (regValue != null)\n    {\n        try\n        {\n            // Convert the registry value to decimal\n            decimal decimalValue = Convert.ToDecimal(regValue);\n            this.apTime.Value = decimalValue;\n        }\n        catch (FormatException ex)\n        {\n            // Handle the format exception if the conversion fails\n            Console.WriteLine("Failed to convert registry value to decimal: " + ex.Message);\n        }\n    }\n    else\n    {\n        Console.WriteLine("Registry key 'apTime' not found.");\n    }\n\n    // Close the registry key\n    APRegsitry.Close();\n}\nelse\n{\n    Console.WriteLine("Registry path 'Software\\YourAppPath' not found.");\n}