// Determine the session timeout from the web.config settings\nint sessionTimeoutInMinutes = (int)Session.Timeout.TotalMinutes;\n\n// Create a new HttpCookie\nHttpCookie cookie = new HttpCookie("sessionGUID");\ncookie.Value = value;\ncookie.Domain = ".example.com";\n\n// Set the expiration to match the session timeout\ncookie.Expires = DateTime.Now.AddMinutes(sessionTimeoutInMinutes);\n\n// Add the cookie to the response\nResponse.Cookies.Set(cookie);