using System.Web.Mvc;\nusing System.Web.Routing;\n\npublic class RouteConfig\n{\n    public static void RegisterRoutes(RouteCollection routes)\n    {\n        routes.IgnoreRoute("{resource}.axd/{*pathInfo}");\n\n        // Custom route for getting bookmarks by username\n        routes.MapRoute(\n            name: "UserBookmarks",\n            url: "users/{username}/bookmarks/",\n            defaults: new { controller = "Bookmark", action = "Index" },\n            constraints: new { httpMethod = new HttpMethodConstraint("GET") }\n        );\n\n        // Custom route for updating a bookmark by username and bookmarkid\n        routes.MapRoute(\n            name: "UpdateUserBookmark",\n            url: "users/{username}/bookmark/{bookmarkid}",\n            defaults: new { controller = "Bookmark", action = "Update" },\n            constraints: new { httpMethod = new HttpMethodConstraint("PUT") }\n        );\n\n        // Default route (optional, usually already present)\n        routes.MapRoute(\n            name: "Default",\n            url: "{controller}/{action}/{id}",\n            defaults: new { controller = "Home", action = "Index", id = UrlParameter.Optional }\n        );\n    }\n}