[HttpPost, ValidateInput(false)]\npublic ActionResult Edit(int id)\n{\n    var product = productsRepository.GetProduct(id);\n\n    if (product == null)\n        return View("NotFound", new MasterViewModel());\n\n    if (TryUpdateModel(product) && UpdateImages(product))\n    {\n        productsRepository.Save();\n        return RedirectToAction("Details", new { id = product.ID });\n    }\n\n    // If model binding fails, return the view with the current product\n    return View(ContentViewModel.Create(product));\n}\n\nprivate bool UpdateImages(Product product)\n{\n    // Assuming Images is a List<ProductImage>\n    for (int i = 0; i < product.ImageList.Count; i++)\n    {\n        string prefix = "images[" + i + "]";\n        if (!TryUpdateModel(product.ImageList[i], prefix, new[] { "Description", "Order" }))\n        {\n            return false;\n        }\n    }\n    return true;\n}