using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic class ReportDataFetcher\n{\n    public DataSet FetchReportData()\n    {\n        string connectionString = "your_connection_string_here";\n        DataTable mainTable = new DataTable();\n        DataTable subreportTable = new DataTable();\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            SqlCommand commandMain = new SqlCommand("YourMainQuery", connection);\n            SqlDataAdapter adapterMain = new SqlDataAdapter(commandMain);\n\n            SqlCommand commandSub = new SqlCommand("YourSubReportQuery", connection);\n            SqlDataAdapter adapterSub = new SqlDataAdapter(commandSub);\n\n            DataSet dataSet = new DataSet();\n            adapterMain.Fill(dataSet, "MainTable");\n            adapterSub.Fill(dataSet, "SubreportTable");\n\n            return dataSet;\n        }\n    }\n}