using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        List<int> numbers = new List<int> { 0, 1, 2, 2, 2, 3, 4, 4, 5 };\n        \n        // Select only the elements that appear exactly once\n        var uniqueNumbers = numbers.GroupBy(n => n)\n                                   .Where(g => g.Count() == 1)\n                                   .Select(g => g.Key)\n                                   .ToList();\n        \n        // Output the result\n        Console.WriteLine(string.Join(", ", uniqueNumbers));\n    }\n}