using System;\nusing System.Diagnostics;\nusing System.Linq;\nusing System.Management;\n\npublic class ProcessOwner\n{\n    public static string GetProcessOwner(int processId)\n    {\n        try\n        {\n            ManagementObjectSearcher searcher = new ManagementObjectSearcher(\n                $"SELECT * FROM Win32_Process WHERE ProcessId={processId}");\n\n            foreach (ManagementObject obj in searcher.Get())\n            {\n                string[] ownerInfo = new string[2];\n                int returnVal = Convert.ToInt32(obj.InvokeMethod("GetOwner", ownerInfo));\n                if (returnVal == 0)\n                {\n                    // OwnerInfo array: [0] - domain, [1] - username\n                    return $"{ownerInfo[1]}@{ownerInfo[0]}";\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred while trying to get the process owner: " + ex.Message);\n        }\n\n        return null;\n    }\n\n    public static void Main()\n    {\n        Process[] processes = Process.GetProcessesByName("MyApp");\n\n        foreach (Process proc in processes)\n        {\n            string owner = GetProcessOwner(proc.Id);\n            Console.WriteLine($"Process ID {proc.Id} - {owner}");\n        }\n    }\n}