using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        // Define the path where you want to create the file\n        string folderPath = "C:\\";\n        \n        for (int i = 0; i < 5; i++)\n        {\n            // Generate a random string of 5 lowercase letters\n            string fileName = GenerateRandomFileName();\n            \n            // Full path to the file\n            string fullPath = Path.Combine(folderPath, fileName + ".txt");\n            \n            try\n            {\n                // Create the text file with some dummy content (or simply create it)\n                File.WriteAllText(fullPath, "This is a test file.");\n                \n                Console.WriteLine($"Created file: {fullPath}");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Error creating file: {ex.Message}");\n            }\n        }\n    }\n\n    static string GenerateRandomFileName()\n    {\n        // Random object\n        Random rand = new Random();\n        \n        // Array to hold the characters of the random string\n        char[] fileNameChars = new char[5];\n        \n        // Loop through and assign a random letter (ASCII code) to each index\n        for (int i = 0; i < fileNameChars.Length; i++)\n        {\n            fileNameChars[i] = (char)('a' + rand.Next(26));\n        }\n        \n        return new string(fileNameChars);\n    }\n}